import { Component } from '@angular/core';
import { Observable } from 'rxjs';

import { User } from 'src/app/data';
import { DataService } from 'src/app/data.service';

@Component({
  selector: 'app-simulation',
  templateUrl: './app-simulation.component.html',
  styleUrls: ['./app-simulation.component.scss'],
})
export class AppSimulationComponent {
  public currentUser$: Observable<User>;

  constructor(private dataService: DataService) {
    this.currentUser$ = this.dataService.currentUser.asObservable();
  }
}
<div class="outer-content" *ngIf="currentUser$ | async as currentUser">
  <div class="phone-border">
    <div class="inner-content">
      <div class="welcome">
        <ion-text color="dark"><h2>Welcome</h2></ion-text>
        <ion-text color="primary"><h2><strong>{{currentUser.name}}</strong></h2></ion-text>
      </div>
      <div class="likes-title">
        <ion-text color="secondary"><h2>Likes <ion-icon name="heart-outline"></ion-icon></h2></ion-text>
      </div>
      <div class="accessory-list">
        <div class="accessory">
          <div class="image-container">
            <img src=""/>
          </div>
          <div class="button-container">
            <ion-button>BUY <ion-icon name="bag-check-outline"></ion-icon></ion-button>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
